import React, { createContext, useState, useEffect } from "react";
import all_product from '../assests/all_product';

export const ShopContext = createContext(null);

console.log("all_product", all_product); // â† add this

const getDefaultCart = () => {
    // Initialize as empty array instead of object
    return [];
}

const ShopContextProvider = (props) => {

    const [cartItems, setCartItems] = useState(getDefaultCart());
    
    useEffect(() => {
        console.log("ðŸ›’ CartItems: ", cartItems);
    }, [cartItems]);

    const addToCart = (itemId, size) => {
        setCartItems((prev) => {
            // Check if item with same ID and size already exists
            const existingItemIndex = prev.findIndex(
                item => item.id === itemId && item.size === size
            );

            if (existingItemIndex !== -1) {
                // If exists, increase quantity
                const updatedItems = [...prev];
                updatedItems[existingItemIndex].quantity += 1;
                console.log("Updated existing cart item:", updatedItems[existingItemIndex]);
                return updatedItems;
            } else {
                // If doesn't exist, add new item
                const newItem = { id: itemId, size: size, quantity: 1 };
                console.log("Added new cart item:", newItem);
                return [...prev, newItem];
            }
        });
    }

    const removeFromCart = (itemId, size) => {
        setCartItems((prev) => {
            const existingItemIndex = prev.findIndex(
                item => item.id === itemId && item.size === size
            );

            if (existingItemIndex !== -1) {
                const updatedItems = [...prev];
                if (updatedItems[existingItemIndex].quantity > 1) {
                    updatedItems[existingItemIndex].quantity -= 1;
                } else {
                    // Remove item completely if quantity becomes 0
                    updatedItems.splice(existingItemIndex, 1);
                }
                return updatedItems;
            }
            return prev;
        });
    }

    // Helper function to get product by ID
    const getProductById = (productId) => {
        return all_product.find((product) => product.id === Number(productId));
    }

    const getTotalCartAmount = () => {
        let totalAmount = 0;
        cartItems.forEach(cartItem => {
            const itemInfo = getProductById(cartItem.id);
            if (itemInfo) {
                totalAmount += itemInfo.new_price * cartItem.quantity;
            }
        });
        return totalAmount;
    }

    const getTotalCartItems = () => {
        return cartItems.reduce((total, item) => total + item.quantity, 0);
    }

    // Helper function to get quantity of specific item and size
    const getCartItemQuantity = (itemId, size) => {
        const item = cartItems.find(item => item.id === itemId && item.size === size);
        return item ? item.quantity : 0;
    }

    // Helper function to check if item exists in cart (for any size)
    const isItemInCart = (itemId) => {
        return cartItems.some(item => item.id === itemId);
    }

    // Function to get all cart items with their product details
    const getCartItemsWithDetails = () => {
        return cartItems.map(cartItem => {
            const productInfo = getProductById(cartItem.id);
            return {
                ...cartItem,
                productInfo: productInfo
            };
        });
    }

    const contextValue = { 
        getTotalCartAmount, 
        getTotalCartItems, 
        all_product, 
        cartItems, 
        addToCart, 
        removeFromCart,
        getCartItemQuantity,
        isItemInCart,
        getCartItemsWithDetails,
        getProductById
    };

    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    )
}

export default ShopContextProvider;
