// In your ShopContext.js file, update the addToCart function and cart state

import React, { createContext, useState } from "react";

export const ShopContext = createContext(null);

const ShopContextProvider = (props) => {
    // Change cart structure to store objects instead of just quantities
    const [cartItems, setCartItems] = useState([]);

    const addToCart = (itemId, size) => {
        setCartItems(prev => {
            // Check if item with same ID and size already exists
            const existingItemIndex = prev.findIndex(
                item => item.id === itemId && item.size === size
            );

            if (existingItemIndex !== -1) {
                // If exists, increase quantity
                const updatedItems = [...prev];
                updatedItems[existingItemIndex].quantity += 1;
                return updatedItems;
            } else {
                // If doesn't exist, add new item
                return [...prev, { id: itemId, size: size, quantity: 1 }];
            }
        });
    };

    const removeFromCart = (itemId, size) => {
        setCartItems(prev => {
            const existingItemIndex = prev.findIndex(
                item => item.id === itemId && item.size === size
            );

            if (existingItemIndex !== -1) {
                const updatedItems = [...prev];
                if (updatedItems[existingItemIndex].quantity > 1) {
                    updatedItems[existingItemIndex].quantity -= 1;
                } else {
                    updatedItems.splice(existingItemIndex, 1);
                }
                return updatedItems;
            }
            return prev;
        });
    };

    const getTotalCartAmount = () => {
        let totalAmount = 0;
        cartItems.forEach(cartItem => {
            // You'll need to get product info by ID to calculate total
            // This depends on how you store your product data
            const product = getProductById(cartItem.id); // You need to implement this
            if (product) {
                totalAmount += product.new_price * cartItem.quantity;
            }
        });
        return totalAmount;
    };

    const getTotalCartItems = () => {
        return cartItems.reduce((total, item) => total + item.quantity, 0);
    };

    // Helper function to get quantity of specific item and size
    const getCartItemQuantity = (itemId, size) => {
        const item = cartItems.find(item => item.id === itemId && item.size === size);
        return item ? item.quantity : 0;
    };

    const contextValue = {
        cartItems,
        addToCart,
        removeFromCart,
        getTotalCartAmount,
        getTotalCartItems,
        getCartItemQuantity
    };

    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    );
};

export default ShopContextProvider;
