import React, { useContext } from 'react';
import { ShopContext } from '../Context/ShopContext';
import './Cart.css'; // Make sure you have appropriate CSS

const Cart = () => {
    const { cartItems, addToCart, removeFromCart, getTotalCartAmount, getCartItemsWithDetails } = useContext(ShopContext);

    const cartItemsWithDetails = getCartItemsWithDetails();

    if (cartItems.length === 0) {
        return (
            <div className="cart-empty">
                <h2>Your Cart is Empty</h2>
                <p>Add some items to get started!</p>
            </div>
        );
    }

    return (
        <div className="cart">
            <h2>Shopping Cart</h2>
            <div className="cart-items">
                {cartItemsWithDetails.map((item, index) => {
                    const { productInfo } = item;
                    
                    if (!productInfo) {
                        return null; // Skip if product not found
                    }

                    return (
                        <div key={`${item.id}-${item.size}-${index}`} className="cart-item">
                            <div className="cart-item-image">
                                <img src={productInfo.image} alt={productInfo.name} />
                            </div>
                            
                            <div className="cart-item-details">
                                <h3 className="cart-item-name">{productInfo.name}</h3>
                                <p className="cart-item-size">
                                    <span>Size: </span>
                                    <strong>{item.size}</strong>
                                </p>
                                <p className="cart-item-price">
                                    ${productInfo.new_price}
                                </p>
                            </div>
                            
                            <div className="cart-item-quantity">
                                <button 
                                    className="quantity-btn"
                                    onClick={() => removeFromCart(item.id, item.size)}
                                >
                                    âˆ’
                                </button>
                                <span className="quantity-display">{item.quantity}</span>
                                <button 
                                    className="quantity-btn"
                                    onClick={() => addToCart(item.id, item.size)}
                                >
                                    +
                                </button>
                            </div>
                            
                            <div className="cart-item-total">
                                <p>${(productInfo.new_price * item.quantity).toFixed(2)}</p>
                            </div>
                        </div>
                    );
                })}
            </div>
            
            <div className="cart-summary">
                <div className="cart-total">
                    <h3>Total: ${getTotalCartAmount().toFixed(2)}</h3>
                </div>
                <button className="checkout-btn">
                    Proceed to Checkout
                </button>
            </div>
        </div>
    );
};

export default Cart;
