import React, { useContext } from "react";
import './ProductDisplay.css'
import star_icon from '../assests/star_icon.png';
import star_dull_icon from '../assests/star_dull_icon.png';
import { ShopContext } from "../Context/ShopContext";

const ProductDisplay = (props) => {
    const { product } = props;
    const { addToCart } = useContext(ShopContext);
    
    // Add safety check for product
    if (!product) {
        return <div>Product not found</div>;
    }
    
    return (
        <div className="productdisplay container">
            <div className="productdisplay-left">
                <div className="productdisplay-img-list">
                    <img src={product.image} alt="" />
                    <img src={product.image} alt="" />
                    <img src={product.image} alt="" />
                    <img src={product.image} alt="" />
                </div>
                <div className="productdisplay-img">
                    <img className='productdisplay-main-img' src={product.image} alt="" />
                </div>
            </div>
            <div className="productdisplay-right">
                <h1>{product.name}</h1>
                <div className="productdisplay-right-stars">
                    <img src={star_icon} alt="" />
                    <img src={star_icon} alt="" />
                    <img src={star_icon} alt="" />
                    <img src={star_icon} alt="" />
                    <img src={star_dull_icon} alt="" />
                    <p>(122)</p>
                </div>
                <div className="productdisplay-right-prices">
                    <div className="productdisplay-right-price-old">${product.old_price}</div>
                    <div className="productdisplay-right-price-new">${product.new_price}</div>
                </div>
                <div className="productdisplay-right-description">
                    {/* Use dynamic description from product data */}
                    {product.description || "No description available"}
                </div>
                <div className="productdisplay-right-size">
                    <h1>Select Size</h1>
                    <div className="productdisplay-right-size-list">
                        <div>S</div>
                        <div>M</div>
                        <div>L</div>
                        <div>XL</div>
                        <div>XXL</div>
                    </div>
                </div>
                <button onClick={()=>{addToCart(product.id)}}>ADD TO CART</button>
                
                {/* Use dynamic category from product data */}
                <p className="productdisplay-right-category">
                    <span>Category :</span>{product.category || "Not specified"}
                </p>
                
                {/* Use dynamic tags from product data */}
                <p className="productdisplay-right-category">
                    <span>Tags :</span>{product.tags || "Modern, Latest"}
                </p>
            </div>
        </div>
    )
}
export default ProductDisplay;
